--CREATE TABLE TEST_TABLE (ID INTEGER NOT NULL IDENTITY, VALUE VARCHAR(256));

CREATE TABLE IF NOT EXISTS PNB_NOTICE_TRX (
	-- IDX INTEGER NOT NULL IDENTITY(START WITH 1),
	IDX INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1), 
	TITLE VARCHAR(500) NOT NULL,
	USER_NICK_NAME VARCHAR(256) NOT NULL,
	USER_PW VARCHAR(256) NOT NULL,
	VIEW_CNT INTEGER DEFAULT 0 NOT NULL,
	CREATE_DT TIMESTAMP DEFAULT current_timestamp NOT NULL,
	MODIFY_DT TIMESTAMP DEFAULT current_timestamp NOT NULL,
	CONTENTS VARCHAR(4000) NOT NULL,
	PRIMARY KEY (IDX)
);
